// New Item
export const addCart = (product) => {
    return {
        type: "ADDITEM",
        payload: product
    }
}

// Delete Item
export const delCart = (product) => {
    return {
        type: "DELITEM",
        payload: product
    }
}

// increment counter
export const incrementCart = (count) => {
    return {
        type: "INCREMENT",
        payload: count
    }
}
