const cart = [];

const handleCart = (state = cart, action) => {

    switch (action.type) {
        case "ADDITEM":
            return [
                ...state,
                action.payload
            ];
            break;

        case "DELITEM":
            return state = state.filter((x) => {
                return x.identification !== action.payload.identification
            });
            break;

        case "INCREMENT":

            const found = state.find((x) => {
                return x.identification === action.payload.identification
            });

            const increment = found.count === undefined ? 1 : found.count;

            state = state.filter((x) => {
                return x.identification !== action.payload.identification
            });

            action.payload.count = increment++;

            return [
                ...state,
                action.payload
            ];
            break;

        default:
            return state;
            break;
    }
}

export default handleCart;
