const products = [];

const handleCounter = (state = products, action) => {

    switch (action.type) {
        case "INCREMENT":
            const found = state.find((x) => {
                return x.identification === action.payload.identification
            });

            let increment = found === undefined ? 0 : found.count;

            state = state.filter((x) => {
                return x.identification !== action.payload.identification
            });

            let newPayload = {
                ...action.payload,
            };
            newPayload.count = (Integer.parseInt(increment) + 1);

            return [
                ...state,
                newPayload
            ];
            break;

        case "DECREMENT":
            const found1 = state.find((x) => {
                return x.identification === action.payload.identification
            });

            let increment1 = found1 === undefined ? 0 : found1.count;

            state = state.filter((x) => {
                return x.identification !== action.payload.identification
            });

            let newPayload1 = {
                ...action.payload,
            };
            newPayload1.count = (increment1 - 1) < 0 ? 0 : (increment1 - 1);

            return [
                ...state,
                newPayload1
            ];
            break;


        case "WRITE":
            state = state.filter((x) => {
                return x.identification !== action.payload.identification
            });

            let newPayload2 = {
                ...action.payload,
            };
            newPayload2.count = action.payload.increment;

            return [
                ...state,
                newPayload2
            ];
            break;
            

        default:
            return state;
            break;
    }
}

export default handleCounter;
