// New Item
export const addCart = (product) => {
    return {
        type: "ADDITEM",
        payload: product
    }
}

// Delete Item
export const delCart = (product) => {
    return {
        type: "DELITEM",
        payload: product
    }
}

// clear cart
export const clearItem = (product) => {
    return {
        type: "CLEARITEM",
        payload: product
    }
}

// increment counter
export const incrementCounter = () => {
    return {
        type: "INCREMENT",
        payload: null
    }
}

// decrement counter
export const decrementCounter = (product) => {
    return {
        type: "DECREMENT",
        payload: product
    }
}

// decrement counter
export const writeCounter = (product) => {
    return {
        type: "WRITE",
        payload: product
    }
}
