import React, { useState, useEffect } from "react";
import { useDispatch } from "react-redux";
import { addCart, delCart } from '../redux/action';
import Skeleton from 'react-loading-skeleton';
import 'react-loading-skeleton/dist/skeleton.css';
import { NavLink, useParams } from "react-router-dom";

const Product = () => {

    const { id } = useParams();
    const [product, setDataProduct] = useState([]);
    const [loading, setLoading] = useState(false);

    const [cartBtn, setCartBtn] = useState("Agregar al carrito");


    const dispatch = useDispatch();

    const handleCart = (product) => {
        if (cartBtn === "Agregar al carrito") {
            dispatch(addCart(product));
            setCartBtn("Remover del carrito");
        } else {
            dispatch(delCart(product));
            setCartBtn("Agregar al carrito");
        }
    }

    useEffect(() => {
        const getProduct = async () => {
            setLoading(true);

            const response = await fetch(`/api/products/${id}`);
            setDataProduct(await response.clone().json());

            setLoading(false);
        }

        getProduct();

        console.log(product.images)
    }, []);



    const Loading = () => {
        return (
            <>
                <div className="col-md-6">
                    <Skeleton height={450} />
                </div>

                <div className="col-md-6" style={{ lineHeight: 2 }}>
                    <Skeleton height={50} width={300} />
                    <Skeleton height={75} />
                    <Skeleton height={25} width={150} />
                    <Skeleton height={50} />
                    <Skeleton height={150} />
                    <Skeleton height={50} width={100} />
                </div>
            </>
        );
    }

    const ShowProduct = () => {
        return (
            <>
                <div className="col-md-6">
                    {
                        product.thumbnail != null ? <div id="carouselExampleAutoplaying" class="carousel slide" data-bs-ride="carousel">
                            <div class="carousel-inner">
                                <div class="carousel-item active">
                                    <img src={product.images[0]} class="d-block w-100" alt="..." />
                                </div>

                                <div class="carousel-item active">
                                    <img src={product.images[1]} class="d-block w-100" alt="..." />
                                </div>

                                <div class="carousel-item active">
                                    <img src={product.images[2]} class="d-block w-100" alt="..." />
                                </div>

                                {product.images.forEach(element => {
                                    return <div class="carousel-item active">
                                        <img src={element} class="d-block w-100" alt="..." />
                                    </div>
                                })}
                            </div>
                            <button class="carousel-control-prev" type="button" data-bs-target="#carouselExampleAutoplaying" data-bs-slide="prev">
                                <span class="carousel-control-prev-icon" aria-hidden="true"></span>
                                <span class="visually-hidden">Previous</span>
                            </button>
                            <button class="carousel-control-next" type="button" data-bs-target="#carouselExampleAutoplaying" data-bs-slide="next">
                                <span class="carousel-control-next-icon" aria-hidden="true"></span>
                                <span class="visually-hidden">Next</span>
                            </button>
                        </div> : <img src={product.images} alt={product.title} height="400px" width="400px" />
                    }
                </div>

                <div className="col-md-6">
                    <h4 className="text-uppercase text-black-50">
                        {product.category}
                    </h4>

                    <h1 className="display-5">{product.title}</h1>

                    <p className="lead fw-bolder">Rating {product.rating} <i className="fa fa-star"></i></p>

                    <h3 className="display-6 fw-blod my-4">
                        $ {product.price}
                    </h3>

                    <p className="lead">{product.description}</p>
                    <p className="lead">{product.stock != null ? `Solo quedan: ${product.stock}` : null}</p>

                    <button className="btn btn-outline-dark px-4 py-2" onClick={() => handleCart(product)}>{cartBtn}</button>
                    <NavLink to="/cart" className="btn btn-dark ms-2 px-3 py-2">ir carro</NavLink>
                </div>
            </>
        )
    }

    return (
        <div>
            <div className="container py-5">
                <div className="row py-5">
                    {loading ? <Loading /> : <ShowProduct />}
                </div>
            </div>
        </div>
    );
}

export default Product;
