import axios from 'axios'

const cart = [];

const getIp = async () => {
    const res = await axios.get('https://geolocation-db.com/json/')
    return res.data.IPv4;
}

const sendPost = async (product, endpoint) => {
    const ip = await getIp();
    let dbCart = [];
    let request;

    if(product == null){
        request = [
            ...product
        ];
        request.ip = ip;

        request = JSON.stringify(request);
    } else {
        request = ip;
    }

    const requestOptions = {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: request
    };

    fetch(endpoint, requestOptions)
        .then(response => response.json())
        .then(data => dbCart = data);

    return dbCart;
}

const handleCart = async (state = cart, action) => {

    switch (action.type) {
        case "ADDITEM":
            const dbCart = await sendPost(action.payload, 'api/cart/add');

            if (dbCart.status === "Success") {
                return [
                    ...state,
                    action.payload
                ];
            } else {
                return state;
            }
            break;

        case "DELITEM":
            const dbCartDel = sendPost(action.payload, 'api/cart/del');

            if (dbCartDel.status === "Success") {
                return state = state.filter((x) => {
                    return x.identification !== action.payload.identification
                });
            } else {
                return state;
            }
            break;

        case "CLEARITEM":
            const dbCartClear = sendPost(null, 'api/cart/clear');

            if (dbCartDel.status === "Success") {
                return [];
            } else {
                return state;
            };
            break;

        default:
            return state;
            break;
    }
}

export default handleCart;
