const cart = [];

const sendPost = (product, endpoint) => {
    let request;

    if (product != null) {
        request = {
            ...product,
            ip: ""
        };
    } else {
        request = null;
    }

    const requestOptions = {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: request
    };

    fetch(endpoint, requestOptions)
        .then(response => console.log(response.json()))
        .then(data => console.log(data));
}

const handleCart = (state = cart, action) => {

    switch (action.type) {
        case "ADDITEM":
            sendPost(action.payload, 'api/cart/add');

            return [
                ...state,
                action.payload
            ];

            return state;
            break;

        case "DELITEM":
            const dbCartDel = sendPost(action.payload, 'api/cart/del');

            if (dbCartDel.status === "Success") {
                return state = state.filter((x) => {
                    return x.identification !== action.payload.identification
                });
            } else {
                return state;
            }
            break;

        case "CLEARITEM":
            const dbCartClear = sendPost(null, 'api/cart/clear');

            if (dbCartDel.status === "Success") {
                return [];
            } else {
                return state;
            };
            break;

        default:
            return state;
            break;
    }
}

export default handleCart;
