import React, { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { delCart } from '../redux/action';
import Skeleton from 'react-loading-skeleton';
import 'react-loading-skeleton/dist/skeleton.css';

const Cart = () => {

    const state = useSelector((state) => state.handleCart);
    const stateCounter = useSelector((state) => state.handleCart);
    const dispatch = useDispatch();

    const handleClose = (product) => {
        dispatch(delCart(product));
    }

    const CountProduct = (cartItem) => {


        const found = stateCounter.find((x) => {
            return x.identification === "Dummy-21"
        });

        var asd = found.identification
        var asds = cartItem.cartItem

        return (
            <>
                {found === undefined ? 0 : found.count}
            </>
        );
    }

    const cartItems = (cartItem) => {
        return (
            <div className="px-4 bg-light rounded-3" key={cartItem.identification}>
                <div className="container py-4">
                    <button className="btn-close float-end" aria-label="Close" onClick={() => handleClose(cartItem)}></button>

                    <div className="row justify-content-center">
                        <div className="col-md-4">
                            <img src={cartItem.image} alt={cartItem.title} height="200px" width="180px" />
                        </div>
                        <div className="col-md-4">
                            <h3>{cartItem.title}</h3>
                            <p className="lead fw-blod">{cartItem.price}</p>
                            <p className="lead fw-blod"><CountProduct cartItem={cartItem.identification}/></p>
                        </div>
                    </div>

                </div>
            </div>
        )
    }

    const cartEmtpy = () => {
        return (
            <div className="px-4 bg-light rounded-3 py-5">
                <div className="container py-4">
                    <div className="row justify-content-center">
                        <h3>El carrito esta vacio</h3>
                    </div>
                </div>
            </div>
        )
    }

    const buttonBuy = () => {
        return (
            <div className="container my-5">
                <div className="row justify-content-center">
                    <button className="btn btn-outline-dark px-4 py-2 mx-auto" >Comprar</button>
                </div>
            </div>
        )
    }

    return (
        <>

            <div>
                <div className="container py-5">
                    <div className="row py-5">
                        {state.length !== 0 ? state.map(cartItems) : cartEmtpy()}
                        {state.length !== 0 && buttonBuy()}
                    </div>
                </div>
            </div>

        </>
    )

}

export default Cart;
