import React, { useState, useEffect } from "react";
import Skeleton from 'react-loading-skeleton';
import 'react-loading-skeleton/dist/skeleton.css';
import { NavLink, useParams } from "react-router-dom";

const Product = () => {

    const { id } = useParams();
    const [product, setDataProduct] = useState([]);
    const [loading, setLoading] = useState(false);

    useEffect(() => {
        const getProduct = async () => {
            setLoading(true);

            const response = await fetch(`https://fakestoreapi.com/products/${id}`);
            setDataProduct(await response.clone().json());

            setLoading(false);
        }

        getProduct();
    }, []);


    const Loading = () => {
        return (
            <>

                <div className="col-md-6">
                    <Skeleton height={450} />
                </div>

                <div className="col-md-6">

                    <Skeleton height={450} width={450} />

                    <h4 className="text-uppercase text-black-50">
                        {product.category}
                    </h4>

                    <h1 className="display-5">{product.title}</h1>

                    <p className="lead fw-bolder">Rating {product.rating && product.rating.rate} <i className="fa fa-star"></i></p>

                    <h3 className="display-6 fw-blod my-4">
                        $ {product.price}
                    </h3>

                    <p className="lead">{product.description}</p>

                    <button className="btn btn-outline-dark px-4 py-2">Add carro</button>
                    <NavLink to="/cart" className="btn btn-dark ms-2 px-3 py-2">ir carro</NavLink>
                </div>
            </>
        );
    }




    const ShowProduct = () => {
        return (
            <>
                <div className="col-md-6">
                    <img src={product.imange} alt={product.title} height="400px" width="400px" />
                </div>

                <div className="col-md-6">
                    <h4 className="text-uppercase text-black-50">
                        {product.category}
                    </h4>

                    <h1 className="display-5">{product.title}</h1>

                    <p className="lead fw-bolder">Rating {product.rating && product.rating.rate} <i className="fa fa-star"></i></p>

                    <h3 className="display-6 fw-blod my-4">
                        $ {product.price}
                    </h3>

                    <p className="lead">{product.description}</p>

                    <button className="btn btn-outline-dark px-4 py-2">Add carro</button>
                    <NavLink to="/cart" className="btn btn-dark ms-2 px-3 py-2">ir carro</NavLink>
                </div>
            </>
        )
    }

    return (
        <div>
            <div className="container py-5">
                <div className="row py-5">
                    {loading ? <Loading /> : <ShowProduct />}
                </div>
            </div>
        </div>
    );
}

export default Product;
