import React, { useEffect }  from "react";
import { NavLink } from 'react-router-dom';
import { useSelector } from "react-redux";

const Navbar = () => {

    const dispatch = useDispatch();
    
    useEffect(() => {
        const getProducts = async () => {
            setLoading(true);

            //https://fakestoreapi.com/products
            const response = await fetch("/api/products");

            if (componentMounted) {
                setData(await response.clone().json());
                setFilter(await response.json());
                setLoading(false);
            }

            return () => {
                componentMounted = false;
            }
        }

        getProducts();
    }, []);

    const state = useSelector((state) => state.handleCart);

    return (
        <div>
            <nav className="navbar navbar-expand-lg bg-body-tertiary py-3 shadow-sm">
                <div className="container">
                    <NavLink className="navbar-brand fw-blod fs-4" to="/">Store</NavLink>
                    <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
                        <span className="navbar-toggler-icon"></span>
                    </button>
                    <div className="collapse navbar-collapse" id="navbarSupportedContent">
                        <ul className="navbar-nav mx-auto mb-2 mb-lg-0">
                            <li className="nav-item">
                                <NavLink className="nav-link active" aria-current="page" to="/">Home</NavLink>
                            </li>
                            <li className="nav-item">
                                <NavLink className="nav-link" to="/products">Product</NavLink>
                            </li>
                        </ul>
                        <div className="buttons">

                            {state.length <= 0 ?
                                null : <NavLink to="/cart" className="btn btn-outline-dark ms-2">
                                    <i className="fa fa-shopping-cart me-1" /> Carrito ({state.length})
                                </NavLink>}

                        </div>
                    </div>
                </div>
            </nav>
        </div>
    );
}

export default Navbar;
