import React, { useState } from "react";
import { useDispatch, useSelector } from "react-redux";

const Confirm = () => {
    const state = useSelector((state) => state.handleCart);
    const stateCounter = useSelector((state) => state.handleCounter);

    let total = 0;

    const handleSubmit = () => {
        const requestOptions = {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify(stateCounter)
        };
        fetch('api/buy', requestOptions)
            .then(response => response.json())
            .then(data => this.setState(data.id));
    }    

    const cartItems = (cartItem) => {
        const found = stateCounter.find((x) => {
            return x.identification === cartItem.identification
        });

        const precioProducto = cartItem.price * found.count;
        total = total + (cartItem.price * found.count);

        return (
            <tr>
                <td>{cartItem.title}</td>
                <td>{found.count}</td>
                <td>$ {precioProducto}</td>
            </tr>
        )
    }

    const cartEmtpy = () => {
        return (
            <div className="px-4 bg-light rounded-3 py-5">
                <div className="container py-4">
                    <div className="row justify-content-center">
                        <h3>El carrito esta vacio, no se puede confirmar la compra</h3>
                    </div>
                </div>
            </div>
        )
    }

    const buttonBuy = () => {
        return (
            <div className="mt-4">
                <div className="row justify-content-center">
                    <hr />
                    <div className="input-group mb-4">
                        <input className="form-control " type="number" placeholder="Nombre" />
                        <input className="form-control ms-2" type="number" placeholder="Telefono" />
                        <input className="form-control ms-2" type="number" placeholder="Banco" />
                    </div>
                    <button className="btn btn-outline-dark px-4 py-2 mx-auto">Confirmar y finalizar compra</button>
                </div>
            </div>
        )
    }

    return (
        <>
            <div>
                <div className="container py-5">
                    <div className="row">

                        <table class="table table-bordered ">
                            <thead>
                                <tr>
                                    <th scope="col">Producto</th>
                                    <th scope="col">Cantidad</th>
                                    <th scope="col">Precio final</th>
                                </tr>
                            </thead>
                            <tbody>
                                {state.length !== 0 ? state.map(cartItems) : cartEmtpy()}
                                <tr>
                                    <td colspan="2"><b>Total compra</b></td>
                                    <td> $ {total}</td>
                                </tr>
                            </tbody>
                        </table>


                        {state.length !== 0 && buttonBuy()}
                    </div>
                </div>
            </div>
        </>
    )
}

export default Confirm;
