import React, { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { addCart, delCart, incrementCart } from '../redux/action';
import Skeleton from 'react-loading-skeleton';
import 'react-loading-skeleton/dist/skeleton.css';
import { NavLink, useParams } from "react-router-dom";

const Product = () => {

    const state = useSelector((state) => state.handleCounter);

    const { id } = useParams();
    const [product, setDataProduct] = useState([]);
    const [loading, setLoading] = useState(false);

    const [cartBtn, setCartBtn] = useState("Agregar al carrito");


    const dispatch = useDispatch();

    const handleCart = (product, action) => {

        if (action === 1) {
            if (cartBtn === "Agregar al carrito") {
                dispatch(addCart(product));
                setCartBtn("Remover del carrito");
            } else {
                dispatch(delCart(product));
                setCartBtn("Agregar al carrito");
            }
        } else {
            dispatch(incrementCart(product));
        }
    }

    useEffect(() => {
        const getProduct = async () => {
            setLoading(true);

            const response = await fetch(`/api/products/${id}`);
            setDataProduct(await response.clone().json());

            setLoading(false);
        }

        getProduct();
    }, []);



    const Loading = () => {
        return (
            <>
                <div className="col-md-6">
                    <Skeleton height={450} />
                </div>

                <div className="col-md-6" style={{ lineHeight: 2 }}>
                    <Skeleton height={50} width={300} />
                    <Skeleton height={75} />
                    <Skeleton height={25} width={150} />
                    <Skeleton height={50} />
                    <Skeleton height={150} />
                    <Skeleton height={50} width={100} />
                </div>
            </>
        );
    }

    const found = state.find((x) => {
        return x.identification === product.identification
    });

    const ShowProduct = () => {
        return (
            <>
                <div className="col-md-6">
                    <img src={product.thumbnail} alt={product.title} height="400px" width="400px" />
                </div>

                <div className="col-md-6">
                    <h4 className="text-uppercase text-black-50">
                        {product.category}
                    </h4>

                    <h1 className="display-5">{product.title}</h1>

                    <p className="lead fw-bolder">Rating {product.rating} <i className="fa fa-star"></i></p>

                    <h3 className="display-6 fw-blod my-4">
                        $ {product.price}
                    </h3>

                    <p className="lead">{product.description}</p>

                    <p className="lead">{product.stock != null ? `Solo quedan: ${product.stock}` : null}</p>


                    <button className="btn btn-dark px-4 py-2" onClick={() => handleCart(product, 2)}><i className="fa fa-minus" /></button>
                    <input className="form-control ms-2" type="text" placeholder="Cantidad" value={found === undefined ? 0 : found.count} />
                    <button className="btn btn-outline-dark px-4 py-2 ms-2" onClick={() => handleCart(product, 2)}><i className="fa fa-plus" /></button>

                    <br />

                    <button className="btn btn-outline-dark px-4 py-2 mt-3" onClick={() => handleCart(product, 1)}>{cartBtn}</button>
                    <NavLink to="/cart" className="btn btn-dark ms-2 px-3 py-2 mt-3">ir carro</NavLink>
                </div>
            </>
        )
    }

    return (
        <div>
            <div className="container py-5">
                <div className="row py-5">
                    {loading ? <Loading /> : <ShowProduct />}
                </div>
            </div>
        </div>
    );
}

export default Product;
