const cart = [];

const handleCart = (state = cart, action) => {

    switch (action.type) {
        case "ADDITEM":
            return [
                ...state,
                action.payload
            ];
            break;

        case "DELITEM":
            return state = state.filter((x) => {
                return x.id !== action.payload.id
            });
            break;

        case "INCREMENT":
            
            var news = state.filter((x) => {
                return x.id === action.payload.id
            });

            state = {
                ...news,
                counter: 19
            };

            return state;            
            break;

        default:
            return state;
            break;
    }
}

export default handleCart;
