import React, { useState, useEffect } from "react";
import { NavLink } from "react-router-dom";
import Skeleton from 'react-loading-skeleton';
import 'react-loading-skeleton/dist/skeleton.css';


const Products = () => {

    const [data, setData] = useState([]);
    const [filter, setFilter] = useState(data);
    const [search, setSearch] = useState("");
    const [loading, setLoading] = useState(false);

    let componentMounted = true;

    useEffect(() => {
        const getProducts = async () => {
            setLoading(true);

            //https://fakestoreapi.com/products
            const response = await fetch("/api/products");

            if (componentMounted) {
                setData(await response.clone().json());
                setFilter(await response.json());
                setLoading(false);
            }

            return () => {
                componentMounted = false;
            }
        }

        getProducts();
    }, []);


    const Loading = () => {
        return (
            <>
                <div className="col-md-3">
                    <Skeleton height={350} />
                </div>
                <div className="col-md-3">
                    <Skeleton height={350} />
                </div>
                <div className="col-md-3">
                    <Skeleton height={350} />
                </div>
                <div className="col-md-3">
                    <Skeleton height={350} />
                </div>
            </>
        );
    }


    const filterProduct = () => {
        const updateList = data.filter((x) => x.title.toLowerCase().includes(document.getElementById("title").value));
        setFilter(updateList);
    };

    const ShowProducts = () => {
        return (
            <>
                <div className="buttons d-flex justify-content-center mb-5 pb-5">


                    <form class="form-inline" id="miformulario">
                        <label class="sr-only" for="usuario">Usuario</label>
                        <input type="text" class="form-control mb-2 mr-sm-2" id="usuario" placeholder="jrc2020" required />
                        <label class="sr-only" for="contrase単a">Contrase単a</label>
                        <div class="input-group mb-2 mr-sm-2">
                            <input type="password" class="form-control" id="contrase単a" placeholder="Contrase単a" required />
                        </div>
                        <div class="form-check mb-2 mr-sm-2">
                            <input class="form-check-input" type="checkbox" id="recordar" />
                            <label class="form-check-label" for="recordar">
                                Recordar
                            </label>
                        </div>
                        <button type="submit" class="btn btn-primary mb-2">Aceptar</button>
                    </form>

                    <div className="mb-4">
                        <label for="title" className="form-label">Filtrar por nombre</label>
                    </div>
                    <div className="mr-4">
                        <input type="text" className="form-control" id="title" placeholder="Digite nombre del producto" />
                    </div>

                    <div className="mb-4">
                        <label for="title" className="form-label">Filtrar por nombre</label>
                    </div>

                    <div className="mb-4">
                        <input type="text" className="form-control" id="precioMenor" placeholder="Digite precio menor" />
                    </div>

                    <div className="mb-4">
                        <input type="text" className="form-control" id="precioMayor" placeholder="Digite precio mayor" />
                    </div>


                    <div className="mb-4">
                        <button className="btn btn-dark me-2" onClick={() => filterProduct("men's clothing")}>Filtar</button>
                        <button className="btn btn-outline-dark me-2" onClick={() => setFilter(data)}>Limpiar filtros</button>
                    </div>

                    {/* <button className="btn btn-outline-dark me-2" onClick={() => setFilter(data)}>All</button>
                    <button className="btn btn-outline-dark me-2" onClick={() => filterProduct("men's clothing")}>Men's</button>
                    <button className="btn btn-outline-dark me-2">Men's</button>
                    <button className="btn btn-outline-dark me-2">Men's</button>
                    <button className="btn btn-outline-dark me-2">Men's</button>
                    <button className="btn btn-outline-dark me-2">Men's</button> */}
                </div>

                {filter.map((product) => {
                    return (
                        <>
                            <div className="col-md-3 mb-4">
                                <div className="card h-100 text-center p-4" key={product.identification}>
                                    <img src={product.images[0]} className="card-img-top" alt={product.title} height="300px" />
                                    <div className="card-body">
                                        <h5 className="card-title">{product.title.substring(0, 12)}...</h5>
                                        <p className="card-text">{product.price}</p>
                                        <NavLink to={`/product/${product.identification}`} className="btn btn-primary">Buy</NavLink>
                                    </div>
                                </div>
                            </div>
                        </>
                    )
                })}
            </>
        )
    }

    return (
        <div>
            <div className="container my-5 py-5">
                <div className="row">
                    <div className="col-12 mb-5">
                        <h1 className="display-6 fw-bolder text-center">Lastest Products</h1>
                        <hr />
                    </div>

                    <div className="row justify-content-center">
                        {loading ? <Loading /> : <ShowProducts />}
                    </div>
                </div>
            </div>
        </div>
    );
}

export default Products;
