const cart = [];

const sendPost = async(product, endpoint) => {
    let dbCart = [];
    let request;

    if(product == null){
        request = JSON.stringify(product);
    } else {
        request = null;
    }

    const requestOptions = {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: request
    };

    fetch(endpoint, requestOptions)
        .then(response => response.json())
        .then(data => dbCart = data);

    return dbCart;
}

const handleCart = (state = cart, action) => {

    switch (action.type) {
        case "ADDITEM":
            sendPost(action.payload, 'api/cart/add').then(result => {
                if (result.status === "Success") {
                    return [
                        ...state,
                        action.payload
                    ];
                } else {
                    return state;
                }
            });
            
            return state;
            
            break;

        case "DELITEM":
            const dbCartDel = sendPost(action.payload, 'api/cart/del');

            if (dbCartDel.status === "Success") {
                return state = state.filter((x) => {
                    return x.identification !== action.payload.identification
                });
            } else {
                return state;
            }
            break;

        case "CLEARITEM":
            const dbCartClear = sendPost(null, 'api/cart/clear');

            if (dbCartDel.status === "Success") {
                return [];
            } else {
                return state;
            };
            break;

        default:
            return state;
            break;
    }
}

export default handleCart;
